function popupku() {
    var pWm = document.createElement("a");
    pWm.setAttribute("href", "https://youtube.com/dekatutorial");
    pWm.setAttribute("style", "display: none;"); // Mengubah properti style untuk menyembunyikan elemen
    pWm.innerHTML = "Youtube: Deka Tutorial";
  
    pWm.addEventListener("click", function (event) {
      event.preventDefault(); // Mencegah URL dari tautan terbuka
    });
  
    document.body.appendChild(pWm);
  
    pWm.remove(); // Menghapus elemen tautan dari DOM
  }
   
  